<script>
module.exports = {
	name: "BodyMap",
	data: function() {
		return {
			selected: '',
			bodyRegions: ['head', 'waist', 'torso', 'feet', 'legs', ''],
			selectedItem: {},
			data: []
		};
	},
	mounted() {
		this.$root.$once('data-is-loaded', (d) => {
			this.data = d;
			// eslint-disable-next-line no-console
			// console.log("got data");
		});
		this.$root.$on('did-select-item', (item) => {
			this.selectedItem = item;
		});
	},
	methods: {
		changeBodySelection: function(area) {
			this.selected = area;
			this.$root.$emit('body-area-selected', area);
		},
		dataForBodyArea: function(area) {
			return this.data.filter(element => ((element.placement == area) || (Array.isArray(element.placement) && element.placement.includes(area)))); 
		},
		getPositionForTypeAndItem: function (type, index, number) {
			// Get the path along which the items should be distributed
			let path = this.$refs[type];
			let itemSeparation = path.getTotalLength() / number;
			let position = path.getPointAtLength(itemSeparation*index);
			return { transform: 'translate(' + position.x + 'px,'+ position.y +'px)' }
		},
		isSelected: function (area) {
			return this.selected === area;
		},
		isSelectedItem: function (itemName) {
			return itemName === this.selectedItem.name;
		}
	},
	computed: {
		style () {
			return { transform: 'translate(' + this.getRndInteger(-10,40) + 'px,' + this.getRndInteger(-20,40) +'px)'}
		}
  }
};
</script>

<style scoped>
	#bodyMap {
		width: 20%;
		top: 0px;
		position: sticky;
	}
	#bodyoutline {
		fill:none; 
		stroke: #384c6c;
		stroke-width: 8px;
	}
	.outline { 
		fill:none; 
		stroke: #384c6c;
		stroke-width: 1px;
	}
	.background {
		fill: #f5f6f8;
	}

	g.selected path.background { fill: green; }
	g:hover path.background { fill: blue; }

	circle {
		stroke-width: 2px;
	}

	circle.bodyItemSelector {
		fill: #A9CE38;
		stroke: #384c6c;
	}
	circle.selected {
		stroke: #EB1B65;
	}

	circle.selectedItem {
		stroke-width: 4px;
		fill: #EB1B65;
		transform: scale(2.0,2.0);
	}
	.positioning {
		display: none;
	}
</style>